function deleteErrorNotification() {
    const array = ["username_error", "email_error", "password_error"];

    for (let index = 0; index < array.length; index++) {
        const error_check = document.querySelector(`.${array[index]}`);
        if (error_check) {
            document.body.removeChild(error_check);
        }
    }

    setTimeout(() => {
        const username_error_notification = document.getElementsByClassName("username_error")[0];
        const email_error_notification = document.getElementsByClassName("email_error")[0];
        const password_error_notification = document.getElementsByClassName("password_error")[0];
        
        
        // Удаление класса анимации и добавление класса для второй анимации
        if (username_error_notification) {
            username_error_notification.classList.remove("tinUpIn");
            username_error_notification.classList.add("tinUpOut");
            setTimeout(() => {
                document.body.removeChild(username_error_notification);
            }, 10000);
        }

        else if (email_error_notification) {
            email_error_notification.classList.remove("tinUpIn");
            email_error_notification.classList.add("tinUpOut");
            setTimeout(() => {
                document.body.removeChild(email_error_notification);
            }, 10000);
        }

        else if (password_error_notification) {
            password_error_notification.classList.remove("tinUpIn");
            password_error_notification.classList.add("tinUpOut");
            setTimeout(() => {
                document.body.removeChild(password_error_notification);
            }, 10000);
        }
    }, 5000);
}

export default deleteErrorNotification;
