// Файл, где настраивается соединение с базой данных (например, database.js)
const mongoose = require("mongoose");

const { MongoClient, ServerApiVersion } = require('mongodb');

const uri = "mongodb+srv://first_user:1111@cluster0.jtz9col.mongodb.net/?retryWrites=true&w=majority";
// Create a MongoClient with a MongoClientOptions object to set the Stable API version
const client = new MongoClient(uri, {
    serverApi: {
        version: ServerApiVersion.v1,
        strict: true,
        deprecationErrors: true,
    }
});

mongoose.connect(uri, {
    useNewUrlParser: true,
    useUnifiedTopology: true,
})

module.exports = mongoose; // Экспортируем объект mongoose, чтобы его можно было импортировать в других файлах
