function checkLoginData(event) {
    event.preventDefault();

    const form = document.getElementById("log_in_form");
    const formData = new FormData(form);

    const username_or_email = formData.get("username");
    const password = formData.get("password");

    const mongoose = require("mongoose");

    // Подключение к базе данных
    mongoose.connect("mongodb://localhost/your-database-name", {
        useNewUrlParser: true,
        useUnifiedTopology: true,
    });

    const db = mongoose.connection;

    db.on("error", console.error.bind(console, "Ошибка подключения к MongoDB:"));
    db.once("open", () => {
        console.log("Успешное подключение к MongoDB");
    });

    const UserModel = require('../db_stuff/User'); // Подключаем модель пользователя

    // Поиск пользователя по имени или email
    UserModel.findOne({ $or: [{ username: 'desiredUsername' }, { email: 'desiredEmail' }] }, (err, user) => {
        if (err) {
            console.error('Ошибка при поиске пользователя:', err);
            // Обработка ошибки
        }

        if (user) {
            console.log('Пользователь найден:', user);
            // Пользователь найден, выполняйте нужные действия
        } else {
            console.log('Пользователь не найден');
            // Пользователь не найден, выполняйте нужные действия
        }
    });
}

export default checkLoginData;
