import "../style/magic.css/dist/magic.css";

function sendInfo(event) {
    event.preventDefault(); // Предотвращаем стандартное поведение отправки формы

    const form = document.getElementById("form");
    const formData = new FormData(form);

    // Получаем значения из полей формы
    const username = formData.get("username");
    const email = formData.get("email");
    const password = formData.get("password");

    const username_input = document.getElementById("username");
    const email_input = document.getElementById("email");
    const password_input = document.getElementById("password");

    const body = document.querySelector("body");

    function displayError(errorClass, errorMessage) {
        const existingError = document.querySelector(errorClass);
        
        // Удаляем существующий элемент с таким классом, если он есть
        if (existingError) {
            document.body.removeChild(existingError);
        }
    
        const error_notification = document.createElement("div");
        error_notification.className = ""
    
        const error_message = document.createTextNode(errorMessage);
        error_notification.appendChild(error_message);
    
        document.body.appendChild(error_notification);
    
        // Запускаем анимацию
        error_notification.classList.add('magictime', 'puffIn');
    }
    
    if (username === "") {
        displayError(".username_error", "Please enter your username!");
    }
    
    if (email === "") {
        displayError(".email_error", "Please enter your email!");
    }
    
    if (password === "") {
        displayError(".password_error", "Please enter your password!");
    }
    


    // Проверяем, что значения получены корректно
    console.log("Username:", username);
    console.log("Email:", email);
    console.log("Password:", password);

    // Отправляем данные на сервер
    fetch("http://localhost:5000/submit", {
        method: "POST",
        body: JSON.stringify({ username, email, password }),
        headers: {
            "Content-Type": "application/json",
        },
    })
        .then(response => response.json())
        .then(data => {
            console.log(`Response Data:`, data);
        })
        .catch(error => {
            console.error("Ошибка при отправке данных:", error);
        });
}



export default sendInfo;
