// Импортируйте модель пользователя, если она еще не импортирована
const User = require("../db_stuff/"); // Подставьте путь к модели пользователя

async function getAvatar(userId) {
    try {
        // Найдите пользователя по ID или другому уникальному идентификатору
        const user = await User.findById(userId);

        // Если пользователь не найден, верните путь к аватару по умолчанию
        if (!user) {
            return "../resources/images/test_avatar.jpg"; // Путь к аватару по умолчанию
        }

        // Если у пользователя есть аватар, верните его
        if (user.avatar) {
            return user.avatar;
        } else {
            return "../resources/images/test_avatar.jpg"; // Путь к аватару по умолчанию
        }
    } catch (error) {
        console.error("Error fetching user avatar:", error);
        return "../resources/images/test_avatar.jpg"; // В случае ошибки верните путь к аватару по умолчанию
    }
}

module.exports = getAvatar;
