import React from "react";
import { BrowserRouter, Route, Routes, Navigate} from "react-router-dom";
import './style/registration.css'
import Registration from './pages/Registration';
import LogIn from './pages/Log_in'
import Home from './pages/Home'
import Friends from './pages/Friends'


function App() {
  function checkToken() {
    const token = localStorage.getItem('token'); // Здесь 'token' - имя, под которым токен сохранен
  
    return !!token; // Преобразуем токен в булевое значение: true, если токен есть, и false, если его нет
  }

  const isAuthenticated = checkToken(); // Проверка наличия токена
  return (
    <BrowserRouter >
      <Routes>
      <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/home" /> : <Registration />}
          index
        />
        <Route path="login" element={<LogIn />} />
        <Route path="registration" element={<Registration />} />
        <Route path="home" element={<Home/>} />
        <Route path="friends" element={<Friends/>} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
