// Файл, где настраивается соединение с базой данных (например, database.js)
const mongoose = require("mongoose");

mongoose.connect("mongodb+srv://first_user:1111@cluster0.jtz9col.mongodb.net/?retryWrites=true&w=majority", {
  useNewUrlParser: true,
  useUnifiedTopology: true,
});

const db = mongoose.connection;

db.on("error", console.error.bind(console, "Ошибка подключения к MongoDB:"));
db.once("open", () => {
  console.log("Успешное подключение к MongoDB");
});

module.exports = mongoose; // Экспортируем объект mongoose, чтобы его можно было импортировать в других файлах
